package main

import (
	"fmt"
	"strings"
)

func countVowels(word string) (ans int64) {
	n := len(word)
	for i, ch := range word {
		if strings.ContainsRune("aeiou", ch) {
			fmt.Println(string(ch))
			ans += int64((i + 1) * (n - i))
		}
	}
	return
}
func main(){
	fmt.Println(countVowels("aba"))
	//test:=""
	//fmt.Println(countVowels(test))
}